// pop-up.js
import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { addItem, updateItem } from "../redux/cartSlice";
import { fetchOptions, fetchOptionDetails } from "../utils/api";

const ItemPopup = ({ item, onClose, editingItem }) => {
    const dispatch = useDispatch();
    const [quantity, setQuantity] = useState(1);
    const [selectedOptions, setSelectedOptions] = useState([]);
    const [selectedSize, setSelectedSize] = useState(item.defaultSize);
    const [options, setOptions] = useState([]);
    const [optionDetails, setOptionDetails] = useState([]);

    useEffect(() => {
        if (editingItem) {
            setQuantity(editingItem.quantity);
            setSelectedOptions(editingItem.options);
            setSelectedSize(editingItem.size);
        }
    }, [editingItem]);

    useEffect(() => {
        fetchOptions().then((data) => {
            if (data && data.status === "success") {
                setOptions(data.options.filter(opt => opt.parent_menu_item === item.id));
            }
        });

        fetchOptionDetails().then((data) => {
            if (data && data.status === "success") {
                setOptionDetails(data.optionDetails);
            }
        });
    }, [item.id]);

    const handleOptionChange = (optionId) => {
        setSelectedOptions((prevOptions) =>
            prevOptions.includes(optionId)
                ? prevOptions.filter((id) => id !== optionId)
                : [...prevOptions, optionId]
        );
    };

    const handleSizeChange = (size) => {
        setSelectedSize(size);
    };

    const extraOptionsPrice = selectedOptions.reduce((sum, optionId) => {
        const option = optionDetails.find((opt) => opt.parent_option === optionId);
        return sum + (option ? option.price : 0);
    }, 0);

    const sizePriceDifference = item.sizes?.find((s) => s.name === selectedSize)?.price || 0;
    const finalPrice = parseFloat(item.price) + sizePriceDifference + extraOptionsPrice;

    const handleAddToCart = () => {
        const payload = {
            id: item.id,
            name: item.name,
            basePrice: item.price,
            finalPrice: finalPrice.toFixed(2),
            options: selectedOptions,
            size: selectedSize,
            quantity,
        };

        if (editingItem) {
            dispatch(updateItem({ id: editingItem.id, newOptions: selectedOptions, newSize: selectedSize, newQuantity: quantity }));
        } else {
            dispatch(addItem(payload));
        }

        onClose();
    };

    return (
        <div className="popup-container">
            <div className="popup">
                <h2>{item.name}</h2>
                <p>Base Price: £{parseFloat(item.price).toFixed(2)}</p>

                <h3>Size</h3>
                <div>
                    {item.sizes && item.sizes.map((size) => (
                        <div key={size.name}>
                            <input
                                type="radio"
                                id={`size-${size.name}`}
                                checked={selectedSize === size.name}
                                onChange={() => handleSizeChange(size.name)}
                            />
                            <label htmlFor={`size-${size.name}`}>{size.name} {size.price > 0 && `(+£${size.price.toFixed(2)})`}</label>
                        </div>
                    ))}
                </div>

                <h3>Options</h3>
                {options.length > 0 ? (
                    options.map((option) => (
                        <div key={option.id}>
                            <input
                                type={option.option_type === 'radio' ? "radio" : "checkbox"}
                                id={`option-${option.id}`}
                                checked={selectedOptions.includes(option.id)}
                                onChange={() => handleOptionChange(option.id)}
                            />
                            <label htmlFor={`option-${option.id}`}>{option.option_name}</label>
                        </div>
                    ))
                ) : (
                    <p>No options available.</p>
                )}

                <div className="quantity-controls">
                    <button onClick={() => setQuantity((q) => (q > 1 ? q - 1 : 1))}>-</button>
                    <span>{quantity}</span>
                    <button onClick={() => setQuantity((q) => q + 1)}>+</button>
                </div>

                <p>Total Price: £{finalPrice.toFixed(2)}</p>

                <button className="btn btn-primary" onClick={handleAddToCart}>
                    {editingItem ? "Update" : "Add"}
                </button>
                <button className="btn btn-secondary" onClick={onClose}>
                    Cancel
                </button>
            </div>
        </div>
    );
};

export default ItemPopup;
