import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateItem, removeItem } from "../redux/cartSlice";
import ItemPopup from "../components/ItemPopup";

const Basket = () => {
    const dispatch = useDispatch();
    const basket = useSelector((state) => state.cart.items) || [];
    const [deliveryMethod, setDeliveryMethod] = useState("pickup");
    const [pickupTime, setPickupTime] = useState("ASAP (15 minutes)");
    const [deliveryAddress, setDeliveryAddress] = useState("");
    const [selectedItem, setSelectedItem] = useState(null);

    const handleQuantityChange = (item, amount) => {
        if (item.quantity + amount > 0) {
            dispatch(updateItem({ id: item.id, newQuantity: item.quantity + amount }));
        }
    };

    const handleRemoveItem = (itemId) => {
        dispatch(removeItem(itemId));
    };

    const openPopup = (item) => {
        setSelectedItem(item);
    };

    const closePopup = () => {
        setSelectedItem(null);
    };

    const totalPrice = basket.reduce((sum, item) => sum + parseFloat(item.finalPrice) * item.quantity, 0).toFixed(2);

    return (
        <div className="basket-container">
            <div className="delivery-method">
                <button className={deliveryMethod === "delivery" ? "active" : ""} onClick={() => setDeliveryMethod("delivery")}>
                    Delivery
                </button>
                <button className={deliveryMethod === "pickup" ? "active" : ""} onClick={() => setDeliveryMethod("pickup")}>
                    Pick-up
                </button>
            </div>

            {deliveryMethod === "pickup" ? (
                <div className="pickup-time">
                    <label>Pickup Time:</label>
                    <select value={pickupTime} onChange={(e) => setPickupTime(e.target.value)}>
                        <option>ASAP (15 minutes)</option>
                        <option>30 minutes</option>
                        <option>45 minutes</option>
                    </select>
                </div>
            ) : (
                <div className="delivery-address">
                    <label>Delivery Address:</label>
                    <input type="text" value={deliveryAddress} onChange={(e) => setDeliveryAddress(e.target.value)} placeholder="Enter your address" />
                </div>
            )}

            <h2>Your Order</h2>
            {basket.length === 0 ? <p>Your basket is empty.</p> : (
                basket.map((item, index) => (
                    <div key={index} className="basket-item">
                        <h3>{item.name}</h3>
                        <p>{item.size}</p>
                        <ul>
                            {(item.options || []).map((optionId) => (
                                <li key={optionId}>{optionId}</li>
                            ))}
                            {(item.optionDetails || []).map((detailId) => (
                                <li key={detailId}>{detailId}</li>
                            ))}
                        </ul>
                        <div className="item-controls">
                            <button onClick={() => handleRemoveItem(item.id)}>🗑️</button>
                            <span>{item.quantity}</span>
                            <button onClick={() => handleQuantityChange(item, 1)}>+</button>
                            <button onClick={() => openPopup(item)}>✏️ Edit</button>
                        </div>
                        <p className="price">£{(parseFloat(item.finalPrice) * item.quantity).toFixed(2)}</p>
                    </div>
                ))
            )}

            <button className="checkout-button">Checkout £{totalPrice}</button>
            {selectedItem && <ItemPopup item={selectedItem} onClose={closePopup} editingItem={selectedItem} />}
        </div>
    );
};

export default Basket;
