import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addItem } from "../redux/cartSlice";
import { useSearchParams } from "react-router-dom";
import "../style.css";

const Menu = () => {
  const dispatch = useDispatch();
  const [searchParams] = useSearchParams();
  const selectedCategory = searchParams.get("category"); // URL'den kategori al
  
  const [categories, setCategories] = useState([]);
  const [menuItems, setMenuItems] = useState([]);
  const basket = useSelector((state) => state.cart.items);

  // âœ… API'den kategorileri ve menÃ¼yÃ¼ Ã§ek
  useEffect(() => {
    fetch("/api/categories/")
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setCategories(data.categories);
        } else {
          console.error("Category Fetch Error:", data.message);
        }
      });

    fetch("/api/menu-items/")
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setMenuItems(data.menu);
        } else {
          console.error("Menu Fetch Error:", data.message);
        }
      });
  }, []);

  const handleAddToBasket = (item) => {
    dispatch(addItem(item));
  };

  return (
    <div className="menu-page">
      {/* ðŸŸ¢ Kategori Filtreleri */}
      <div className="category-filter">
        {categories.length === 0 ? (
          <p>Loading categories...</p>
        ) : (
          categories.map((category) => (
            <a
              key={category.id}
              href={`?category=${category.name.toLowerCase()}`}
              className={`category-btn ${selectedCategory === category.name.toLowerCase() ? "active" : ""}`}
            >
              {category.name}
            </a>
          ))
        )}
      </div>

      {/* ðŸ”µ MenÃ¼ Ã–ÄŸeleri */}
      <div className="menu-container">
        {categories.length === 0 || menuItems.length === 0 ? (
          <p>Loading menu items...</p>
        ) : (
          categories.map((category) =>
            (!selectedCategory || selectedCategory === category.name.toLowerCase()) ? (
              <div key={category.id} className="category-section">
                <h2>{category.name}</h2>
                <div className="menu-grid">
                  {menuItems
                    .filter((item) => item.category === category.id)
                    .map((item) => (
                      <div key={item.id} className="menu-item-card">
                        <img src={item.photo} alt={item.name} className="menu-item-image" />
                        <h4>{item.name}</h4>
                        <p className="price">Â£{item.price.toFixed(2)}</p>
                        <button className="btn btn-primary" onClick={() => handleAddToBasket(item)}>
                          Add to Cart
                        </button>
                      </div>
                    ))}
                </div>
              </div>
            ) : null
          )
        )}
      </div>

      {/* ðŸ›’ Sepet */}
      <div className="basket-container">
        <h2>Your Basket</h2>
        {basket.length === 0 ? (
          <p>Your basket is empty.</p>
        ) : (
          <ul className="basket-list">
            {basket.map((item, index) => (
              <li key={index} className="basket-item">
                <span>{item.name}</span>
                <span className="price">Â£{item.price.toFixed(2)}</span>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Menu;
