import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addItem } from "../redux/cartSlice";
import { useSearchParams } from "react-router-dom";
import "../style.css";
import { fetchCategories, fetchMenuItems } from "../utils/api"; // ðŸ”¥ Yeni API dosyasÄ±nÄ± import ettik!

const Menu = () => {
  const dispatch = useDispatch();
  const [searchParams] = useSearchParams();
  const selectedCategory = searchParams.get("category");

  const [categories, setCategories] = useState([]);
  const [menuItems, setMenuItems] = useState([]);
  const basket = useSelector((state) => state.cart.items);

  useEffect(() => {
    // âœ… API Ã§aÄŸrÄ±larÄ±nÄ± merkezi hale getirdik
    fetchCategories().then((data) => {
      if (data && data.status === "success") setCategories(data.categories);
    });

    fetchMenuItems().then((data) => {
      if (data && data.status === "success") setMenuItems(data.menu);
    });
  }, []);

  return (
    <div className="menu-page container">
      <div className="row">
        <div className="col-md-3 mt-4">
          <h2>Categories</h2>
          {categories.map((category) => (
            <div key={category.id} className="list-group">
              <a href={`?category=${category.id}`} className="list-group-item list-group-item-action">
                {category.name}
              </a>
            </div>
          ))}
        </div>

        <div className="col-md-6 mt-4">
          <h2>Menu</h2>
          {categories.map((category) =>
            (!selectedCategory || parseInt(selectedCategory) === category.id) ? (
              <div key={category.id} className="category-section">
                <hr />
                <h4>{category.name}</h4>
                {menuItems
                  .filter((item) => item.category.id === category.id)
                  .map((item) => (
                    <div key={item.id} className="card mb-3">
                      <div className="card-body">
                        <h5 className="card-title">{item.name}</h5>
                        <p className="card-text">Â£{parseFloat(item.price).toFixed(2)}</p>
                      </div>
                    </div>
                  ))}
              </div>
            ) : null
          )}
        </div>
      </div>
    </div>
  );
};

export default Menu;
