// menu.js
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addItem, updateItem, removeItem } from "../redux/cartSlice";
import { useSearchParams } from "react-router-dom";
import "../style.css";
import { fetchCategories, fetchMenuItems, fetchOptions, fetchOptionDetails } from "../utils/api";
import ItemPopup from "../components/ItemPopup";

const Menu = () => {
    const dispatch = useDispatch();
    const [searchParams] = useSearchParams();
    const selectedCategory = searchParams.get("category");

    const [categories, setCategories] = useState([]);
    const [menuItems, setMenuItems] = useState([]);
    const [options, setOptions] = useState([]);
    const [optionDetails, setOptionDetails] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const basket = useSelector((state) => state.cart.items);

    useEffect(() => {
        fetchCategories().then((data) => {
            if (data && data.status === "success") setCategories(data.categories);
        });

        fetchMenuItems().then((data) => {
            if (data && data.status === "success") setMenuItems(data.menu);
        });

        fetchOptions().then((data) => {
            if (data && data.status === "success") setOptions(data.options);
        });

        fetchOptionDetails().then((data) => {
            if (data && data.status === "success") setOptionDetails(data.optionDetails);
        });
    }, []);

    const openPopup = (item) => {
        setSelectedItem(item);
    };

    const closePopup = () => {
        setSelectedItem(null);
    };

    return (
        <div className="menu-page container">
            <div className="row">
                <div className="col-md-3 category-menu">
                    <h2>Categories</h2>
                    {categories.map((category) => (
                        <div key={category.id} className="list-group">
                            <a href={`?category=${category.id}`} className="list-group-item list-group-item-action">
                                {category.name}
                            </a>
                        </div>
                    ))}
                </div>

                <div className="col-md-6 menu-items">
                    <h2>Menu</h2>
                    {categories.map((category) =>
                        (!selectedCategory || parseInt(selectedCategory) === category.id) ? (
                            <div key={category.id} className="category-section">
                                <hr />
                                <h4>{category.name}</h4>
                                {menuItems
                                    .filter((item) => item.category.id === category.id)
                                    .map((item) => (
                                        <div key={item.id} className="card menu-item">
                                            {item.photo ? (
                                                <img src={item.photo} className="card-img-top" alt={item.name} />
                                            ) : (
                                                <div className="no-image">No Image</div>
                                            )}
                                            <div className="card-body">
                                                <h5 className="card-title">{item.name}</h5>
                                                <p className="card-text">£{parseFloat(item.price).toFixed(2)}</p>
                                                <button className="btn btn-primary" onClick={() => openPopup(item)}>Customize</button>
                                            </div>
                                        </div>
                                    ))}
                            </div>
                        ) : null
                    )}
                </div>
                
                <div className="col-md-3 cart-section">
                    <h2>Basket</h2>
                    {basket.length === 0 ? <p>Your basket is empty.</p> : (
                        basket.map((item, index) => (
                            <div key={index} className="cart-item">
                                <p>{item.name} - {item.quantity}x</p>
                                <p>£{item.finalPrice}</p>
                            </div>
                        ))
                    )}
                </div>
            </div>
            {selectedItem && <ItemPopup item={selectedItem} onClose={closePopup} />}
        </div>
    );
};

export default Menu;
