import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addItem } from "../redux/cartSlice";
import { useSearchParams } from "react-router-dom";
import "../style.css";

const Menu = () => {
  const dispatch = useDispatch();
  const [searchParams] = useSearchParams();
  const selectedCategory = searchParams.get("category");

  const [categories, setCategories] = useState([]);
  const [menuItems, setMenuItems] = useState([]);
  const basket = useSelector((state) => state.cart.items);
  const [selectedDeliveryOption, setSelectedDeliveryOption] = useState("delivery"); // ‚úÖ Delivery/Pickup se√ßimi i√ßin state
  const [selectedOptions, setSelectedOptions] = useState({}); // ‚úÖ Opsiyonlarƒ± y√∂netmek i√ßin state

  useEffect(() => {
    fetch("/api/categories/")
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setCategories(data.categories);
        }
      });

    fetch("/api/menu-items/")
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setMenuItems(data.menu);
        }
      });
  }, []);

  const handleAddToBasket = (item) => {
    dispatch(addItem(item));
  };

  const handleOptionSelect = (itemId, optionId) => {
    setSelectedOptions({
      ...selectedOptions,
      [itemId]: optionId,
    });
  };

  return (
    <div className="menu-page container">
      {/* üü¢ Kategori Filtreleri */}
      <div className="row">
        <div className="col-md-3 mt-4">
          <h2>Categories</h2>
          {categories.map((category) => (
            <div key={category.id} className="list-group">
              <a href={`?category=${category.name.toLowerCase()}`} className="list-group-item list-group-item-action">
                {category.name}
              </a>
            </div>
          ))}
        </div>

        {/* üîµ Men√º √ñƒüeleri */}
        <div className="col-md-6 mt-4">
          <h2>Menu</h2>
          {categories.map((category) =>
            (!selectedCategory || selectedCategory === category.name.toLowerCase()) ? (
              <div key={category.id} className="category-section">
                <hr />
                <h4>{category.name}</h4>
                {menuItems
                  .filter((item) => item.category === category.id)
                  .map((item) => (
                    <div key={item.id} className="card mb-3">
                      <div className="card-body">
                        <h5 className="card-title">{item.name}</h5>
                        <p className="card-text">¬£{item.price.toFixed(2)}</p>

                        {/* Opsiyonlar i√ßin Modal */}
                        {item.hasOption ? (
                          <div>
                            <select
                              onChange={(e) => handleOptionSelect(item.id, e.target.value)}
                              className="form-select mb-2"
                            >
                              <option value="">Select an option</option>
                              {item.options &&
                                item.options.map((option) => (
                                  <option key={option.id} value={option.id}>
                                    {option.option_name} (+¬£{option.price})
                                  </option>
                                ))}
                            </select>
                          </div>
                        ) : null}

                        <button className="btn btn-primary" onClick={() => handleAddToBasket(item)}>
                          Add to Cart
                        </button>
                      </div>
                    </div>
                  ))}
              </div>
            ) : null
          )}
        </div>

        {/* üõí Sepet */}
        <div className="col-md-3 mt-4">
          <h2>Basket</h2>
          <div className="card">
            <ul className="list-group list-group-flush">
              {basket.length === 0 ? (
                <p>Your basket is empty.</p>
              ) : (
                basket.map((item, index) => (
                  <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                    <span>{item.name}</span>
                    <span className="price">¬£{item.price.toFixed(2)}</span>
                  </li>
                ))
              )}
            </ul>
          </div>

          {/* üöÄ Teslimat veya Restorandan Alma Se√ßimi */}
          <h2>Select Option</h2>
          <div className="btn-group w-100" role="group">
            <input
              type="radio"
              className="btn-check"
              name="btnradio"
              id="delivery"
              autoComplete="off"
              checked={selectedDeliveryOption === "delivery"}
              onChange={() => setSelectedDeliveryOption("delivery")}
            />
            <label className="btn btn-outline-success flex-fill" htmlFor="delivery">
              Delivery
            </label>

            <input
              type="radio"
              className="btn-check"
              name="btnradio"
              id="pickup"
              autoComplete="off"
              checked={selectedDeliveryOption === "pickup"}
              onChange={() => setSelectedDeliveryOption("pickup")}
            />
            <label className="btn btn-outline-success flex-fill" htmlFor="pickup">
              Pick-up
            </label>
          </div>

          {/* üè† Adres veya Restoran Se√ßimi */}
          {selectedDeliveryOption === "delivery" ? (
            <div className="form-floating mt-3">
              <input type="text" className="form-control" placeholder="Enter Address" />
              <label>Enter Address</label>
            </div>
          ) : (
            <p className="mt-3">Pick up from the restaurant.</p>
          )}

          <hr />
          <h5>Total: ¬£{basket.reduce((acc, item) => acc + item.price, 0).toFixed(2)}</h5>
          <button className="btn btn-primary w-100 mt-3">Checkout</button>
        </div>
      </div>
    </div>
  );
};

export default Menu;
