import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { addItem, updateItem } from "../redux/cartSlice";

const ItemPopup = ({ item, onClose, editingItem }) => {
    const dispatch = useDispatch();
    const [quantity, setQuantity] = useState(1);
    const [selectedOptions, setSelectedOptions] = useState([]);
    const [selectedOptionDetails, setSelectedOptionDetails] = useState([]);
    const [selectedSize, setSelectedSize] = useState(item.defaultSize);
    
    // ✅ Sadece ilgili `options` ve `option details`ları filtrele
    const itemOptions = item.options || [];  

    useEffect(() => {
        if (editingItem) {
            setQuantity(editingItem.quantity);
            setSelectedOptions(editingItem.options);
            setSelectedOptionDetails(editingItem.optionDetails || []);
            setSelectedSize(editingItem.size);
        }
    }, [editingItem]);

    const handleOptionChange = (optionId) => {
        setSelectedOptions((prevOptions) =>
            prevOptions.includes(optionId)
                ? prevOptions.filter((id) => id !== optionId)
                : [...prevOptions, optionId]
        );
    };

    const handleOptionDetailChange = (optionDetailId) => {
        setSelectedOptionDetails((prevDetails) =>
            prevDetails.includes(optionDetailId)
                ? prevDetails.filter((id) => id !== optionDetailId)
                : [...prevDetails, optionDetailId]
        );
    };

    // ✅ Seçili `options` ve `optionDetails` fiyatlarını hesapla
    const extraOptionsPrice = selectedOptions.reduce((sum, optionId) => {
        const option = itemOptions.find((opt) => opt.id === optionId);
        return sum + (option ? option.option_details.reduce((acc, detail) => acc + (parseFloat(detail.price) || 0), 0) : 0);
    }, 0);

    const extraOptionDetailsPrice = selectedOptionDetails.reduce((sum, detailId) => {
        const detail = itemOptions.flatMap(opt => opt.option_details).find(d => d.id === detailId);
        return sum + (detail ? parseFloat(detail.price) || 0 : 0);
    }, 0);

    const sizePriceDifference = parseFloat(item.sizes?.find((s) => s.name === selectedSize)?.price) || 0;
    const basePrice = parseFloat(item.price) || 0;
    
    const finalPrice = basePrice + sizePriceDifference + extraOptionsPrice + extraOptionDetailsPrice;

    const handleAddToCart = () => {
        const payload = {
            id: item.id,
            name: item.name,
            basePrice: basePrice.toFixed(2),
            finalPrice: finalPrice.toFixed(2),
            options: selectedOptions,
            optionDetails: selectedOptionDetails,
            size: selectedSize,
            quantity,
        };

        if (editingItem) {
            dispatch(updateItem({ id: editingItem.id, newOptions: selectedOptions, newOptionDetails: selectedOptionDetails, newSize: selectedSize, newQuantity: quantity }));
        } else {
            dispatch(addItem(payload));
        }

        onClose();
    };

    return (
        <div className="popup-container">
            <div className="popup">
                <h2>{item.name}</h2>
                <p>Base Price: £{basePrice.toFixed(2)}</p>

                <h3>Size</h3>
                <div>
                    {item.sizes && item.sizes.map((size) => (
                        <div key={size.name}>
                            <input
                                type="radio"
                                id={`size-${size.name}`}
                                checked={selectedSize === size.name}
                                onChange={() => handleSizeChange(size.name)}
                            />
                            <label htmlFor={`size-${size.name}`}>{size.name} {size.price > 0 && `(+£${parseFloat(size.price).toFixed(2)})`}</label>
                        </div>
                    ))}
                </div>

                <h3>Options</h3>
                {itemOptions.map((option) => (
                    <div key={option.id} className="option-group">
                        <strong>{option.option_name}</strong>
                        <div>
                            <input
                                type={option.option_type === 'radio' ? "radio" : "checkbox"}
                                id={`option-${option.id}`}
                                checked={selectedOptions.includes(option.id)}
                                onChange={() => handleOptionChange(option.id)}
                            />
                            <label htmlFor={`option-${option.id}`}>{option.option_name}</label>
                        </div>

                        {/* Option Details (Ekstra Malzemeler) */}
                        {option.option_details.length > 0 && (
                            <div className="option-details">
                                <small>Extra Toppings:</small>
                                {option.option_details.map((detail) => (
                                    <div key={detail.id} className="option-detail">
                                        <input
                                            type="checkbox"
                                            id={`detail-${detail.id}`}
                                            checked={selectedOptionDetails.includes(detail.id)}
                                            onChange={() => handleOptionDetailChange(detail.id)}
                                        />
                                        <label htmlFor={`detail-${detail.id}`}>{detail.optionDetail_name} (+£{parseFloat(detail.price).toFixed(2)})</label>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                ))}

                <p>Total Price: £{finalPrice.toFixed(2)}</p>

                <button className="btn btn-primary" onClick={handleAddToCart}>Add to Basket</button>
                <button className="btn btn-secondary" onClick={onClose}>Cancel</button>
            </div>
        </div>
    );
};

export default ItemPopup;
