import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { addToBasket } from "../redux/cartSliceSlice"; // Redux Sepet Aksiyonları
import { Link } from "react-router-dom";

const Home = () => {
  const dispatch = useDispatch();
  const [categories, setCategories] = useState([]);
  const [menuItems, setMenuItems] = useState([]);
  const [basket, setBasket] = useState([]);

  // API'den kategorileri ve menüyü çek
  useEffect(() => {
    fetch("/api/categories/")
      .then((res) => res.json())
      .then((data) => setCategories(data));

    fetch("/api/menu-items/")
      .then((res) => res.json())
      .then((data) => setMenuItems(data));
  }, []);

  // Sepete ekleme işlemi
  const handleAddToBasket = (item) => {
    dispatch(addToBasket(item));
    setBasket([...basket, item]);
  };

  return (
    <div className="container">
      <div className="row">
        {/* Kategoriler */}
        <div className="col-md-2 mt-4">
          <h2>Categories</h2>
          <div className="list-group">
            {categories.map((category) => (
              <a
                key={category.id}
                href={`#category-${category.id}`}
                className="list-group-item list-group-item-action"
              >
                {category.name}
              </a>
            ))}
          </div>
        </div>

        {/* Menü Öğeleri */}
        <div className="col-md-6 mt-4">
          <h2>Menu</h2>
          {categories.map((category) => (
            <div key={category.id} className="category" id={`category-${category.id}`}>
              <hr />
              <h4>{category.name}</h4>
              {menuItems
                .filter((item) => item.category === category.id)
                .map((item) => (
                  <div key={item.id} className="card mb-3">
                    <div className="card-body">
                      <h5 className="card-title">{item.name}</h5>
                      <p className="card-text">${item.price.toFixed(2)}</p>
                      <button onClick={() => handleAddToBasket(item)} className="btn btn-primary">
                        Add to Cart
                      </button>
                    </div>
                  </div>
                ))}
            </div>
          ))}
        </div>

        {/* Sepet */}
        <div className="col-md-4 mt-4">
          <h2>Basket</h2>
          <div className="card">
            <ul className="list-group list-group-flush">
              {basket.map((item, index) => (
                <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                  <span>{item.name}</span>
                  <span className="badge badge-success">${item.price.toFixed(2)}</span>
                </li>
              ))}
            </ul>
            <div className="card-footer d-flex justify-content-between align-items-center">
              <Link to="/checkout" className="btn btn-primary">
                Checkout
              </Link>
              <h5 className="card-title mt-2 ml-1">
                Total: ${basket.reduce((acc, item) => acc + item.price, 0).toFixed(2)}
              </h5>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
