const BASE_URL = "http://127.0.0.1:8000/api/menu";

// ✅ Menü öğelerini getir
export const fetchMenuItems = async () => {
    try {
        const res = await fetch(`${BASE_URL}/menu-items/`);
        const data = await res.json();
        return data;
    } catch (error) {
        console.error("Error fetching menu items:", error);
        return null;
    }
};

// ✅ Kategorileri getir
export const fetchCategories = async () => {
    try {
        const res = await fetch(`${BASE_URL}/categories/`);
        const data = await res.json();
        return data;
    } catch (error) {
        console.error("Error fetching categories:", error);
        return null;
    }
};

// ✅ Belirli bir menü öğesinin opsiyonlarını getir
export const fetchOptions = async (itemId) => {
    try {
        const res = await fetch(`${BASE_URL}/menu-options/item/${itemId}/`);
        const data = await res.json();
        return data;
    } catch (error) {
        console.error("Error fetching options:", error);
        return null;
    }
};

// ✅ Belirli bir menü öğesinin opsiyon detaylarını getir
export const fetchOptionDetails = async (itemId) => {
    try {
        const res = await fetch(`${BASE_URL}/option-details/item/${itemId}/`);
        const data = await res.json();
        return data;
    } catch (error) {
        console.error("Error fetching option details:", error);
        return null;
    }
};

// ✅ Belirli bir menü öğesinin yan ürünlerini getir
export const fetchSideSelections = async (itemId) => {
    try {
        const res = await fetch(`${BASE_URL}/side-selections/item/${itemId}/`);
        const data = await res.json();
        return data;
    } catch (error) {
        console.error("Error fetching side selections:", error);
        return null;
    }
};

// ✅ Yeni bir sipariş gönder
export const postOrder = async (orderData) => {
    try {
        const res = await fetch(`${BASE_URL}/order/`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(orderData),
        });
        return await res.json();
    } catch (error) {
        console.error("Error posting order:", error);
        return null;
    }
};
