import React, { useEffect, useState } from "react"; 
import { useDispatch, useSelector } from "react-redux";
import { addItem } from "../redux/cartSlice";
import { useSearchParams } from "react-router-dom";
import "../style.css";

const Menu = () => {
  const dispatch = useDispatch();
  const [searchParams] = useSearchParams();
  const selectedCategory = searchParams.get("category");

  const [categories, setCategories] = useState([]);
  const [menuItems, setMenuItems] = useState([]);
  const basket = useSelector((state) => state.cart.items);

  const [selectedDeliveryOption, setSelectedDeliveryOption] = useState("delivery");
  const [selectedItem, setSelectedItem] = useState(null);
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [quantity, setQuantity] = useState(1);
  const [totalPrice, setTotalPrice] = useState(0);

  // ✅ Backend ile uyumlu API çağrıları
  useEffect(() => {
    fetch("/api/menu/categories/")
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setCategories(data.categories);
        }
      });

      useEffect(() => {
        fetch("http://127.0.0.1:8000/api/menu/categories/")
          .then((res) => {
            if (!res.ok) {
              throw new Error(`HTTP error! Status: ${res.status}`);
            }
            return res.json();
          })
          .then((data) => {
            console.log("Categories API Response:", data);
            if (data.status === "success") {
              setCategories(data.categories);
            }
          })
          .catch((error) => console.error("Error fetching categories:", error));
      
        fetch("http://127.0.0.1:8000/api/menu/menu-items/")
          .then((res) => {
            if (!res.ok) {
              throw new Error(`HTTP error! Status: ${res.status}`);
            }
            return res.json();
          })
          .then((data) => {
            console.log("Menu Items API Response:", data);
            if (data.status === "success") {
              setMenuItems(data.menu);
            }
          })
          .catch((error) => console.error("Error fetching menu items:", error));
      }, []);

    fetch("/api/menu/menu-items/")
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setMenuItems(data.menu);
        }
      });
  }, []);

  // ✅ Modal açıldığında sıfırla ve güncelle
  const handleOpenModal = (item) => {
    setSelectedItem(item);
    setSelectedOptions([]);
    setQuantity(1);
    setTotalPrice(parseFloat(item.price));
  };

  // ✅ Opsiyon seçim işlemleri
  const handleOptionChange = (optionId, optionDetail) => {
    let updatedOptions = [...selectedOptions];

    if (optionDetail.option_type === "radio") {
      updatedOptions = updatedOptions.filter(opt => opt.parent_menu_item !== optionId);
      updatedOptions.push(optionDetail);
    } else if (optionDetail.option_type === "checkbox") {
      const exists = updatedOptions.find(opt => opt.id === optionDetail.id);
      if (exists) {
        updatedOptions = updatedOptions.filter(opt => opt.id !== optionDetail.id);
      } else {
        updatedOptions.push(optionDetail);
      }
    }

    let newTotalPrice = parseFloat(selectedItem.price) * quantity;
    updatedOptions.forEach(opt => {
      newTotalPrice += parseFloat(opt.price) * quantity;
    });

    setSelectedOptions(updatedOptions);
    setTotalPrice(newTotalPrice);
  };

  // ✅ Miktar değiştirme
  const handleQuantityChange = (change) => {
    const newQuantity = Math.max(1, quantity + change);
    setQuantity(newQuantity);

    let newTotalPrice = parseFloat(selectedItem.price) * newQuantity;
    selectedOptions.forEach(opt => {
      newTotalPrice += parseFloat(opt.price) * newQuantity;
    });

    setTotalPrice(newTotalPrice);
  };

  // ✅ Sepete ekleme
  const handleAddToBasket = () => {
    const basketItem = {
      ...selectedItem,
      quantity,
      options: selectedOptions,
      totalPrice,
    };
    dispatch(addItem(basketItem));
    setSelectedItem(null);
  };

  return (
    <div className="menu-page container">
      <div className="row">
        <div className="col-md-3 mt-4">
          <h2>Categories</h2>
          {categories.map((category) => (
            <div key={category.id} className="list-group">
              <a href={`?category=${category.id}`} className="list-group-item list-group-item-action">
                {category.name}
              </a>
            </div>
          ))}
        </div>

        <div className="col-md-6 mt-4">
          <h2>Menu</h2>
          {categories.map((category) =>
            (!selectedCategory || parseInt(selectedCategory) === category.id) ? (
              <div key={category.id} className="category-section">
                <hr />
                <h4>{category.name}</h4>
                {menuItems
                  .filter((item) => item.category.id === category.id)
                  .map((item) => (
                    <div key={item.id} className="card mb-3">
                      <div className="card-body">
                        <h5 className="card-title">{item.name}</h5>
                        <p className="card-text">£{parseFloat(item.price).toFixed(2)}</p>
                        <button className="btn btn-primary" onClick={() => handleOpenModal(item)}>
                          Select Options
                        </button>
                      </div>
                    </div>
                  ))}
              </div>
            ) : null
          )}
        </div>

        <div className="col-md-3 mt-4">
          <h2>Basket</h2>
          <div className="card">
            <ul className="list-group list-group-flush">
              {basket.length === 0 ? (
                <p>Your basket is empty.</p>
              ) : (
                basket.map((item, index) => (
                  <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                    <span>{item.name} ({item.quantity}x)</span>
                    <span className="price">£{parseFloat(item.totalPrice).toFixed(2)}</span>
                  </li>
                ))
              )}
            </ul>
          </div>

          <h2>Select Option</h2>
          <div className="btn-group w-100" role="group">
            <input
              type="radio"
              className="btn-check"
              name="btnradio"
              id="delivery"
              autoComplete="off"
              checked={selectedDeliveryOption === "delivery"}
              onChange={() => setSelectedDeliveryOption("delivery")}
            />
            <label className="btn btn-outline-success flex-fill" htmlFor="delivery">
              Delivery
            </label>

            <input
              type="radio"
              className="btn-check"
              name="btnradio"
              id="pickup"
              autoComplete="off"
              checked={selectedDeliveryOption === "pickup"}
              onChange={() => setSelectedDeliveryOption("pickup")}
            />
            <label className="btn btn-outline-success flex-fill" htmlFor="pickup">
              Pick-up
            </label>
          </div>

          {selectedDeliveryOption === "delivery" ? (
            <div className="form-floating mt-3">
              <input type="text" className="form-control" placeholder="Enter Address" />
              <label>Enter Address</label>
            </div>
          ) : (
            <p className="mt-3">Pick up from the restaurant.</p>
          )}

          <hr />
          <h5>Total: £{basket.reduce((acc, item) => acc + parseFloat(item.totalPrice), 0).toFixed(2)}</h5>
          <button className="btn btn-primary w-100 mt-3">Checkout</button>
        </div>
      </div>
    </div>
  );
};

export default Menu;
